                times: 1

            }, 1500);

        } else {

            $("<p class='redmsg'>" + msg + "</p>").insertBefore("#" + id);

            $("#" + id).prev('p.redmsg').effect("shake", {

                direction: "left",

                times: 1,

                distance: 10

            }, 1500);

        }



        /*setTimeout(function(){

    	$("#"+id).prev().hide()

    },2000) */



    }



